binding.script:
if (getprop("/it-autoflight/input/ap1-avail")) {
	setprop("/it-autoflight/input/ap1-avail", 0);
	setprop("/it-autoflight/input/ap2-avail", 0);
	setprop("/it-autoflight/input/ap3-avail", 0);
	setprop("/it-autoflight/input/ap1", 0);
	setprop("/it-autoflight/input/ap2", 0);
	setprop("/it-autoflight/input/ap3", 0);
} else {
	setprop("/it-autoflight/input/ap1-avail", 1);
	setprop("/it-autoflight/input/ap2-avail", 1);
	setprop("/it-autoflight/input/ap3-avail", 1);
}
